<style lang="scss">
$bleu : #2463AA;
$transition : 0.3s ease-in-out;



.stop-scroll{
    overflow: hidden;
}

.hamburger-menu{
    display: none;
}

nav{
    height: 15vh;
    display: flex;
    justify-content: space-between;
    .logo{
        width: 60%;
        a{  
            cursor: default;
            height: 100%;
            display: flex;
            align-items: center;
            img{
                height: 80%;
                cursor: pointer;
            }
            h1{
                color:$bleu;
                cursor: pointer;
            }
        }
    }
    ul{
        display: flex;
        width: 40%;
        justify-content: space-between;
        align-items: center;
        li{
            a{
                color:black;
                &:hover{
                    color:$bleu;
                    transition: $transition;
                }
                &::after{
                    content: '';
                    display: block;
                    width: 0;
                    height: 2px;
                    background: $bleu;
                    transition: width .3s;
                    margin-top:10px;
                }
                &:hover::after{
                    width: 100%;
                }
            }
            .router-link-exact-active{
                    color:$bleu;
                    &::after{
                        content: '';
                        display: block;
                        width: 100%;
                        height: 2px;
                        background: $bleu;
                        transition: width .3s;
                        margin-top:10px
                    }
                }
        }
    }
    .hamburger{
       display: none;
    }
}
@media (max-width:1024px){
    nav{
        .logo{
            width: 100%;
            display: flex;
            justify-content: center;
        }
        ul{
            display: none;
        }
        .hamburger{
            display: inline;
            position: absolute;
            top:30px;
            right:30px;
            width: 50px;
            cursor: pointer;
        }
        .hamburger-menu{
            display:flex;
            flex-direction: column;
            justify-content: center;
            height: 100vh;
            width: 100vw;
            position: absolute;
            background: linear-gradient(197.4deg, #2463AA 0%, #5AC2D8 100.71%);
            top:0px;
            left:0px;
            transition: 0s;
            svg{
                width: 30%;
                margin: 0 auto 20px auto;
            }
            .close{
                position: absolute;
                top:35px;
                right: 30px;
                svg{
                    width: 50px;
                }
            }
            h1{
                    color:white;
                    font-size:3em;
                    margin: 0 auto 0 auto;
                }
            p{
                margin-left:25%;
                a{
                    color:white;
                }
            }
}
    }
    
}
@media (max-width:480px){
    nav{
        .hamburger{
            width: 30px;
            top:40px
        }
        .hamburger-menu{
            .close{
                svg{
                    width: 30px;
                }
            }
            h1{
                font-size:2em;
            }
            p{
                font-size:1em;

            }
    }
    }
    
}
</style>

<script>
export default {
    name:"NavBar",
    data(){
        return{
            displayHmb :false
        }
    },
    methods:{
        hmbMenu(){
            if(this.displayHmb){
                this.displayHmb = !this.displayHmb
                document.querySelector("body").style.overflow="auto"
                document.querySelector(".fleche").style.display="block"
            }
            else{
                this.displayHmb = !this.displayHmb
                
                document.querySelector("body").style.overflow="hidden"
                document.querySelector(".fleche").style.display="none"
                window.scroll(0,-10000)
            }
        }
        
    }
}
</script>